import React, { Suspense, Component } from 'react';
import { Row, Container, Col, Button } from 'react-bootstrap';
import { Switch, Route, Redirect } from 'react-router-dom';
import SideNav, { Toggle, Nav, NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
import routes from '../../routes/routes';
import '../../assets/css/style.css';
import '@trendmicro/react-sidenav/dist/react-sidenav.css';

class LayoutBased extends Component {
    render() {
        return (
            <Container fluid>
                <nav className='nav-container'>
                    <Row>
                        <Col md={9} className='nav-info-left'>
                            <h3>DAN LIRIS MANAGEMENT SYSTEM</h3>
                        </Col>
                        <Col md={3} className='nav-info-right'>
                            Welcome, <span>Test1</span>
                        </Col>
                    </Row>
                </nav>
                <SideNav
                    onSelect={(selected) => {
                        // Add your code here
                    }}
                >
                    <SideNav.Toggle />
                    <SideNav.Nav defaultSelected="home">
                        <NavItem eventKey="home">
                            <NavIcon>
                                <i className="fa fa-fw fa-home" style={{ fontSize: '1.75em' }} />
                            </NavIcon>
                            <NavText>
                                Home
            </NavText>
                        </NavItem>
                        <NavItem eventKey="charts">
                            <NavIcon>
                                <i className="fa fa-fw fa-line-chart" style={{ fontSize: '1.75em' }} />
                            </NavIcon>
                            <NavText>
                                Charts
            </NavText>
                            <NavItem eventKey="charts/linechart">
                                <NavText>
                                    Line Chart
                </NavText>
                            </NavItem>
                            <NavItem eventKey="charts/barchart">
                                <NavText>
                                    Bar Chart
                </NavText>
                            </NavItem>
                        </NavItem>
                    </SideNav.Nav>
                </SideNav>
                <main className="main">
                    <Container fluid style={{ marginLeft: -15 }}>
                        <Suspense fallback={<div className="animated fadeIn pt-1 text-center">Loading...</div>}>
                            <Switch>
                                {routes.map((route, idx) => {
                                    return route.component ? (
                                        <Route
                                            key={idx}
                                            path={route.path}
                                            name={route.name}
                                            render={props => (
                                                <route.component {...props} />
                                            )} />
                                    ) : (null);
                                })}
                                <Redirect from="/" to="/Dashboard" />
                            </Switch>
                        </Suspense>
                    </Container>
                </main>
            </Container>
        );
    }
}

export default LayoutBased;
