import React from "react";
import { Row, Container, Col, ButtonToolbar, Button, FormControl, FormGroup } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useHistory } from 'react-router-dom';
import '../../assets/css/style.css';
import DataTable from 'react-data-table-component';
import faker from 'faker';
import styled from 'styled-components';
import { storiesOf } from '@storybook/react';

function AkunBank() {
    const history = useHistory();
    function handlerClickDestination(destination) {
        history.push(destination)
    }

    const createUser = () => ({
        id: faker.random.uuid(),
        name: faker.name.findName(),
        email: faker.internet.email(),
        address: faker.address.streetAddress(),
        bio: faker.lorem.sentence(),
        image: faker.image.avatar(),
    });

    const createUsers = (numUsers = 5) =>
        new Array(numUsers).fill(undefined).map(createUser);

    const fakeUsers = createUsers(200);

    const TextField = styled.input`
        height: 32px;
        width: 200px;
        border-radius: 3px;
        border-top-left-radius: 5px;
        border-bottom-left-radius: 5px;
        border-top-right-radius: 0;
        border-bottom-right-radius: 0;
        border: 1px solid #e5e5e5;
        padding: 0 32px 0 16px;
        `;

    const FilterComponent = ({ filterText, onFilter, onClear }) => (
        <>
            <TextField id="search" type="text" placeholder="Filter By Name" value={filterText} onChange={onFilter} />
        </>
    );

    const columns = [
        {
            name: 'Name',
            selector: 'name',
            sortable: true,
        },
        {
            name: 'Email',
            selector: 'email',
            sortable: true,
        },
        {
            name: 'Address',
            selector: 'address',
            sortable: true,
        },
    ];

    const BasicTable = () => {
        const [filterText, setFilterText] = React.useState('');
        const [resetPaginationToggle, setResetPaginationToggle] = React.useState(false);
        const filteredItems = fakeUsers.filter(item => item.name && item.name.includes(filterText));

        const subHeaderComponentMemo = React.useMemo(() => {
            const handleClear = () => {
                if (filterText) {
                    setResetPaginationToggle(!resetPaginationToggle);
                    setFilterText('');
                }
            };

            return <FilterComponent onFilter={e => setFilterText(e.target.value)} onClear={handleClear} filterText={filterText} />;
        }, [filterText, resetPaginationToggle]);


        return (
            <Container fluid>
                <DataTable
                    title="Contact List"
                    columns={columns}
                    data={filteredItems}
                    pagination
                    paginationResetDefaultPage={resetPaginationToggle} // optionally, a hook to reset pagination to page 1
                    subHeader
                    subHeaderComponent={subHeaderComponentMemo}
                    selectableRows
                    persistTableHead
                />
                {/* <Row>
                <h3 className='wel-text'>Akun Bank</h3>
            </Row>
            <Row>
                <Col md={0}>
                    <Button variant="primary">Buat</Button>
                </Col>
                <Col md={3} style={{ left: 800 }}>
                    <FormControl type='text' placeholder='Pencarian...'></FormControl>
                </Col>
            </Row>
            <Row>
                <DataTable noHeader columns={columns} data={data} />
            </Row> */}
            </Container>
        )
    }
}

export default AkunBank;
