import React from "react";
import { Row, Container, Col, Button } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';
import '../../assets/css/style.css';
import DataTable from 'react-data-table-component';


function AkunBank() {
    const history = useHistory();
    function handlerClickDestination(destination) {
        history.push(destination)
    }

    const data = [

        { id: 1, namaAkun: 'BCA', noAkun: '015.29.7344.4', kodeBank: '-', namaBank: 'BCA', alamatBank: '-', kodeMataUang: 'USD', namaDivisi: 'T', COA: '1042.01.0.00' }
    
    ]
    const columns = [
        {
            name: 'Nama Akun',
            selector: 'namaAkun',
            sortable: true,
        },
        {
            name: 'No Akun',
            selector: 'noAkun',
            sortable: true,
        },
        {
            name: 'Kode Bank',
            selector: 'kodeBank',
            sortable: true,
        },
        {
            name: 'Nama Bank',
            selector: 'namaBank',
            sortable: true,
        },
        {
            name: 'Alamat Bank',
            selector: 'alamatBank',
            sortable: true,
        },
        {
            name: 'Kode Mata Uang',
            selector: 'kodeMataUang',
            sortable: true,
        },
        {
            name: 'Nama Divisi',
            selector: 'namaDivisi',
            sortable: true,
        },
    ];

    return (
        <Container fluid>
            <DataTable title="Arnold Movies" columns={columns} data={data}/>
        </Container>
    )
}

export default AkunBank;
